  getItems() {
    return of(this.items);
  }

  getCategories() {
    return of(this.categories);
  }

  getItemById(id: string) {
    return of(this.items.find(item => item.id === id));
  }
  
  addItem(item: MarketplaceItem) {
    this.items.unshift(item);
  }
